sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Ensure the Vehicle Status changed to "Sent for Servicing"
    if (current.vehicle_status == "Sent for Servicing" && previous.vehicle_status != "Sent for Servicing") {

        // Check if an open Vehicle Servicing ticket exists
        var servicingGR = new GlideRecord('x_1515317_lva_vehicle_servicing'); // Replace with your Vehicle Servicing table name
        servicingGR.addQuery('vehicle_tracker', current.sys_id); // Match by Vehicle Tracker
        servicingGR.addQuery('ticket_status', 'Open'); // Look for open tickets
        servicingGR.query();

        if (!servicingGR.hasNext()) {
            // No open ticket found, create a new one
            var newServicingTicket = new GlideRecord('x_1515317_lva_vehicle_servicing'); // Replace with your Vehicle Servicing table name
            newServicingTicket.initialize();

            // Map fields from Vehicle Tracker
            newServicingTicket.setValue('vehicle_tracker', current.sys_id); // Reference to Vehicle Tracker
            newServicingTicket.setValue('vehicle', current.vehicle); // Reference to Vehicle
            newServicingTicket.setValue('vehicle_status', 'In Service'); // Set Vehicle Status to "In Service"
            newServicingTicket.setValue('date_sent_for_servicing', GlideDateTime().getDisplayValue()); // Set the current date/time
            newServicingTicket.setValue('ticket_status', 'Open'); // Set Ticket Status to "Open"

            // Insert the new record
            var newTicketID = newServicingTicket.insert();
            gs.info('New Vehicle Servicing ticket created: ' + newTicketID);
        } else {
            gs.info('An open Vehicle Servicing ticket already exists for Vehicle Tracker: ' + current.sys_id);
        }
    }
})(current, previous);
