sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Variable for the current vehicle status
    var vehicleStatus = current.getValue("vehicle_status");

    // Create a GlideRecord on the Loaner Vehicle Catalog table
    var vehGr = new GlideRecord('x_1515317_lva_loaner_vehicle_catalog');
    vehGr.addQuery('sys_id', current.vehicle); // Assuming 'vehicle' is the reference field linking to the Loaner Vehicle Catalog
    gs.info('Current vehicle status: ' + vehicleStatus); // Log the vehicle status

    // Execute the query
    vehGr.query();
    if (vehGr.next()) {
        // Update the status based on the Vehicle Tracker status
        if (['Pending Release', 'Out on Field', 'Returned for Inspection', 'Sent for Servicing'].includes(vehicleStatus)) {
            vehGr.setValue("status", "Unavailable");
        } else if (vehicleStatus == 'Returned to Warehouse') {
            vehGr.setValue("status", "Available");
        } else if (vehicleStatus == 'Decommissioned') {
            vehGr.setValue("status", "Decommissioned");
        } else {
            gs.info('No status change required for vehicle status: ' + vehicleStatus);
        }

        // Update the Loaner Vehicle Catalog record
        vehGr.update();
        gs.info('Updated Loaner Vehicle Record Status to: ' + vehGr.status); // Debugging: Log the updated status
    } else {
        gs.info('No Loaner Vehicle Record found for vehicle: ' + current.vehicle);
    }

})(current, previous);
