sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Check if the Ticket Status is being changed to "Closed"
    if (current.ticket_status == "Closed" && previous.ticket_status != "Closed") {

        // Validate that the Vehicle Tracker reference exists
        if (current.vehicle_tracker) {
            var trackerGR = new GlideRecord('x_1515317_lva_vehicle_tracker'); // Replace with your Vehicle Tracker table name
            if (trackerGR.get(current.vehicle_tracker)) { // Fetch the associated Vehicle Tracker record

                // Update the Vehicle Tracker fields
                trackerGR.setValue('vehicle_status', current.vehicle_status); // Sync Vehicle Status
                trackerGR.setValue('tracker_status', 'Closed'); // Set Ticket Status to "Closed"

                // Save the changes to the Vehicle Tracker record
                trackerGR.update();

                // Log the update for debugging purposes
                gs.info('Vehicle Tracker updated. Status: ' + trackerGR.vehicle_status + ', Ticket Status: ' + trackerGR.ticket_status);
            } else {
                gs.error('Failed to find Vehicle Tracker record for sys_id: ' + current.number);
            }
        } else {
            gs.error('Vehicle Tracker reference is missing on the Vehicle Servicing record: ' + current.sys_id);
        }
    }
})(current, previous);
